import React from 'react';


export default class Auto extends React.Component{
  constructor (props){
    super(props);
    this.item = [
      'Mingmitr',
      'Cafe amezon',
      'Starbucks Coffee'
    ];
    this.state ={
      suggestions: [],
    };
  }

  onTextChanged = (e) => {
    const value = e.target.value;
    if( value.length ===0){
      this.setState(() => ({
        suggestions: [],
      }));
    }else{
      const regex = new RegExp ('^${value}','i');
    }
  }

  render(){
    return (
      <div>
        <input onChange={this.onTextChanged} type="text"/>
      <ul>
        {this.item.map((item) => <li>{item}</li>)}
      </ul>
      </div>
    )
  }
}
